var intro__parallax = document.getElementById("intro__parallax"),
  parallaxInstance = new Parallax(intro__parallax),
  intro__parallax2 = document.getElementById("intro__parallax2"),
  parallaxInstance = new Parallax(intro__parallax2),
  intro__parallax3 = document.getElementById("intro__parallax3"),
  parallaxInstance = new Parallax(intro__parallax3),
  intro__parallax4 = document.getElementById("intro__parallax4"),
  parallaxInstance = new Parallax(intro__parallax4),
  intro__parallax5 = document.getElementById("intro__parallax5"),
  parallaxInstance = new Parallax(intro__parallax5),
  intro__parallax6 = document.getElementById("intro__parallax6"),
  parallaxInstance = new Parallax(intro__parallax6);
window.addEventListener("DOMContentLoaded", () => {
  let a = () => {
    let a = new IntersectionObserver((a, b) => {
      a.forEach((a) => {
        if (a.isIntersecting) {
          let c = new CountUp(
            a.target.id,
            0,
            a.target.dataset.num,
            0,
            a.target.dataset.duration || 4,
            {
              separator: " ",
              prefix: a.target.dataset.prefix || "",
              suffix: a.target.dataset.suffix || "",
            }
          );
          c.start(() => {
            a.target.parentElement.classList.add("numbers__item_done");
          }),
            b.unobserve(a.target);
        }
      });
    });
    document.querySelectorAll(".numbers__item span").forEach((b) => {
      a.observe(b);
    });
  };
  a();
});
